class NewsResponse {
  NewsResponse({
      String? status,
    String? error,
    String? message,
      num? totalResults, 
      List<News>? newsList,}){
    _status = status;
    _totalResults = totalResults;
    newsList = newsList;
    error=error;
     message= message;
}

  NewsResponse.fromJson(dynamic json) {
    _status = json['status'];
    _totalResults = json['totalResults'];
    if (json['articles'] != null) {
      newsList = [];
      json['articles'].forEach((v) {
        newsList?.add(News.fromJson(v));
      });
    }
  }
  String? _status;
  num? _totalResults;
  List<News>? newsList;
String? message;
String? error;

NewsResponse copyWith({  String? status,
  num? totalResults,
  List<News>? newsList,
}) => NewsResponse(  status: status ?? _status,
  totalResults: totalResults ?? _totalResults,
  newsList: newsList ?? newsList,
);
  String? get status => _status;
  num? get totalResults => _totalResults;
  List<News>? get articles => newsList;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['status'] = _status;
    map['message']=message;
    map['error']=error;
    map['totalResults'] = _totalResults;
    if (newsList != null) {
      map['articles'] = newsList?.map((v) => v.toJson()).toList();
    }
    return map;
  }

